UPDATE AD_Field SET IsAllowCopy = 'Y' WHERE AD_Field.IsDisplayed = 'Y' OR AD_Field.IsDisplayedGrid='Y';
UPDATE AD_Field SET IsAllowCopy = 'N' WHERE AD_Field.IsDisplayed = 'N' OR EXISTS (SELECT 1 FROM AD_Column WHERE AD_Column.AD_Column_ID=AD_Field.AD_Column_ID AND (AD_Column.IsUpdateable='N' OR AD_Reference_ID = 13 OR  
AD_Column.ColumnName IN ('DocAction','DocStatus','Processed','ProcessedOn','Processed','Posted','IsPrinted','IsPrinted','CreatedBy','Created','Updated','UpdatedBy','IsActive','Value','DocumentNo','IsPaid','IsReconciled','IsPrepayment','IsAllocated','IsDelivery','IsSelfService','IsGenerated','GrandTotal','TotalLines')));
